CREATE TABLE IF NOT EXISTS ec_adverts(id BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT, PRIMARY KEY(id), name VARCHAR(80) NOT NULL, price FLOAT(18,2) UNSIGNED NOT NULL, description VARCHAR(250) NULL, url VARCHAR(200) NOT NULL, clicks INTEGER(30) UNSIGNED NOT NULL, stars INTEGER(1) UNSIGNED NOT NULL, status INTEGER(1) UNSIGNED NOT NULL, image VARCHAR(150) NULL, category_id INTEGER(10) UNSIGNED NOT NULL) ENGINE=innodb DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;CREATE TABLE IF NOT EXISTS ec_categories(id BIGINT(20) UNSIGNED NOT NULL AUTO_INCREMENT, PRIMARY KEY(id), name VARCHAR(10) NOT NULL, category_id INTEGER(10) UNSIGNED NOT NULL, description VARCHAR(250) NULL, status INTEGER(1) UNSIGNED NOT NULL DEFAULT 1) ENGINE=innodb DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;